import React, { useEffect, useState } from 'react';
import './Choose.css';
import axios from 'axios';

function Choose() {
  const [choose, setChoose] = useState('');
  const [questions, setQuestions] = useState([]);
  const [currQuestIndex, setCurrQuestIndex] = useState(0);
  const [option, setOption] = useState('');
  const [showQuestions, setShowQuestions] = useState(false); // State to control whether to show questions

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.post('http://localhost:5000/questions', { choice: choose });
        setQuestions(response.data.questions);
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    };
    fetchQuestions();
  }, [choose]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Check if an answer is selected
    const answerSelected = choose !== '';
    const optionSelected = option !== '';
    // Move to the next question if there are more questions available and an answer is selected
    if (currQuestIndex < questions.length - 1 && answerSelected && optionSelected) {
      setCurrQuestIndex(currQuestIndex + 1);
      // Reset the selected answer
      setChoose('');
      setOption('');
    } else {
      // Handle the case when no answer is selected or there are no more questions
      if (!answerSelected || !optionSelected) {
        alert('Please select an answer before proceeding to the next question.');
      } else {
        // Handle form submission or thank you message here
        console.log('Form submitted');
      }
    }
  };

  const handleNextButtonClick = () => {
    setShowQuestions(true); // Show questions when the next button is clicked
  };

  return (
    <div>
      <div className="container">
        <div className="content-div">
          <form>
            <h2 className="text-center mb-5">Business Survey</h2>
            {(!showQuestions && questions.length === 0) && (
              <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
                <label className="form-label chooseQuestion fw-bold mb-5">Are you a restaurant owner or a supplier ?</label> <br />
                <div className='ownerSupplier'>
                  <div>
                    <input type='radio' name='radio' value='restaurant owner' onChange={(e) => setChoose(e.target.value)} />
                    <label>Restaurant Owner</label>
                  </div>
                  <div>
                    <input type='radio' name='radio' value='supplier' onChange={(e) => setChoose(e.target.value)} />
                    <label>Supplier</label>
                  </div>
                </div>
              </div>
            )}
            {(showQuestions && questions.length > 0) && (
              <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
                <label className="form-label chooseQuestion fw-bold mb-5">{questions[currQuestIndex].text}</label> <br />
                <div className='ownerSupplier'>
                  {questions[currQuestIndex].options.map((option, index) => (
                    <div key={index}>
                      <input type='radio' name='radio' value={option} onChange={(e) => setOption(e.target.value)} checked={option === choose} />
                      <label>{option}</label>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {(!showQuestions && questions.length === 0) && (
              <div className="quiz-btn-section">
                <button type='button' className="btn btn-primary second-next-btn" onClick={handleNextButtonClick}>Next</button>
              </div>
            )}
            {(showQuestions && questions.length > 0) && (
              <div className="quiz-btn-section ">
                <button type='submit' className="btn btn-primary second-next-btn" onClick={handleSubmit}>Next</button>
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Choose;






















