import React, { useEffect, useState } from 'react';
import './Choose.css';
import axios from 'axios';

function Choose() {
  const [choose, setChoose] = useState('');
  const [questions, setQuestions] = useState([]);
  const [currQuestIndex, setCurrQuestIndex] = useState(0);
  const [allAnswers, setAllAnswers] = useState([]);
  const [showFinalSubmit, setShowFinalSubmit] = useState(false);

  const fetchQuestions = async () => {
    try {
      const response = await axios.post('http://localhost:5000/questions', { choice: choose });
      setQuestions(response.data.questions);
    } catch (error) {
      console.error('Error fetching questions:', error);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedAnswers = [...allAnswers, { question: questions[currQuestIndex].text, answer: choose }];
    setAllAnswers(updatedAnswers);
    setCurrQuestIndex(currQuestIndex + 1);
    setChoose('');
    if (currQuestIndex === questions.length - 1) {
      setShowFinalSubmit(true);
    }
  };

  const handleFinalSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:5000/save-answers', { answers: allAnswers });
      console.log('Response:', response.data);
      // Handle success
    } catch (error) {
      console.error('Error:', error);
      // Handle error
    }
  };

  return (
    <div>
      <div className="container">
        <div className="content-div">
          <form onSubmit={handleSubmit}>
            <h2 className="text-center mb-5">Business Survey</h2>
            {/* <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
              <label className="form-label chooseQuestion fw-bold mb-5">{questions[currQuestIndex].text}</label>
              <div className='ownerSupplier'>
                {questions[currQuestIndex].options.map((option, index) => (
                  <div key={index}>
                    <input type='radio' name='radio' value={option} onChange={(e) => setChoose(e.target.value)} />
                    <label>{option}</label>
                  </div>
                ))}
              </div>
            </div> */}

{questions.length > 0 && currQuestIndex < questions.length && (
  <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
    <label className="form-label chooseQuestion fw-bold mb-5">{questions[currQuestIndex].text}</label>
    <div className='ownerSupplier'>
      {questions[currQuestIndex].options.map((option, index) => (
        <div key={index}>
          <input type='radio' name='radio' value={option} onChange={(e) => setChoose(e.target.value)} />
          <label>{option}</label>
        </div>
      ))}
    </div>
  </div>
)}
            <div className="quiz-btn-section ">
              {showFinalSubmit ? (
                <button type='button' className="btn btn-primary second-next-btn" onClick={handleFinalSubmit}>Submit Answers</button>
              ) : (
                <button type='submit' className="btn btn-primary second-next-btn">Next</button>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Choose;










