import React, { useEffect, useState } from 'react';
import './Choose.css';
import axios from 'axios';

function Choose() {
  const [choose, setChoose] = useState('');
  const [questions, setQuestions] = useState([]);
  const [currQuestIndex, setCurrQuestIndex] = useState(0);
  const [isLastQuestion, setIsLastQuestion] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.post('http://localhost:5000/questions', { choice: choose });
        setQuestions(response.data.questions);
        setSubmitted(false); // Reset submitted status
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    };
    fetchQuestions();
  }, [choose]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Check if an answer is selected
    if (choose === '') {
      alert('Please select an answer before proceeding to the next question.');
      return;
    }
    // Store answer in backend
    await storeAnswer();
    // Move to the next question if there are more questions available
    if (currQuestIndex < questions.length - 1) {
      setCurrQuestIndex(currQuestIndex + 1);
      setChoose('');
    } else {
      // Handle the case when all questions are answered
      setSubmitted(true);
    }
  };

  const storeAnswer = async () => {
    try {
      await axios.post('http://localhost:5000/answers', { answer: choose });
      console.log('Answer stored successfully');
    } catch (error) {
      console.error('Error storing answer:', error);
    }
  };

  useEffect(() => {
    setIsLastQuestion(currQuestIndex === questions.length - 1);
  }, [currQuestIndex, questions]);

  return (
    <div>
      <div className="container">
        <div className="content-div">
          <form onSubmit={handleSubmit}>
            <h2 className="text-center mb-5">Business Survey</h2>
            {!submitted && questions.length > 0 && (
              <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
                <label className="form-label chooseQuestion fw-bold mb-5">{questions[currQuestIndex].text}</label> <br />
                <div className='ownerSupplier'>
                  {questions[currQuestIndex].options.map((option, index) => (
                    <div key={index}>
                      <input type='radio' name='radio' value={option} onChange={(e) => setChoose(e.target.value)} checked={choose === option} />
                      <label>{option}</label>
                    </div>
                  ))}
                </div>
              </div>
            )}
            {!submitted && (
              <div className="quiz-btn-section">
                {isLastQuestion ? (
                  <button type='submit' className="btn btn-primary second-next-btn" disabled={choose === ''}>
                    {submitted ? 'Submitted' : 'Submit'}
                  </button>
                ) : (
                  <button type='button' className="btn btn-primary second-next-btn" onClick={handleSubmit} disabled={choose === ''}>
                    Next
                  </button>
                )}
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Choose;















