import React from 'react';

function Questions({ text, options, handleChoose }) {
  return (
    <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
      <label className="form-label chooseQuestion fw-bold mb-5">{text}</label> <br />
      <div className='ownerSupplier' style={{ width: '100%', display: 'flex', flexDirection: 'column' }}>
        {options.map((option, index) => (
          <div key={index} >
            <input type='radio' name='radio' value={option} onChange={(e) => handleChoose(e.target.value)} />
            <label>{option}</label> 
          </div>
        ))} 
      </div>
    </div>
  );
}

export default Question;
