import React, { useEffect, useState } from 'react';
import './Choose.css';
import axios from 'axios';

function Choose() {
  const [choose, setChoose] = useState('');
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [submitted, setSubmitted] = useState(false); // Track form submission

  const handleSubmit = async (e) => {
    e.preventDefault();
    const answer = document.querySelector('input[name="option"]:checked')?.value;
    setAnswers([...answers, answer]);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    } else {
      // Submit the answers
      try {
        const response = await axios.post('http://localhost:5000/submit-answers', { answers });
        console.log('Response from server:', response.data);
        setSubmitted(true); // Update state to show "Thank You" message
      } catch (error) {
        console.error('Error submitting answers:', error);
      }
    }
  };

  useEffect(() => {
    // Fetch questions based on user selection
    fetchQuestions();
  }, [choose]);

  const fetchQuestions = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/questions/${choose}`);
      setQuestions(response.data.questions);
    } catch (error) {
      console.error('Error fetching questions:', error);
    }
  };

  return (
    <div>
      <div className="container">
        <div className="content-div">
          {!submitted ? ( // Render form if not submitted
            <form>
              <h2 className="text-center mb-5">Business Survey</h2>
              <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
                <label className="form-label chooseQuestion fw-bold mb-5">Are you a restaurant owner or a supplier ?</label> <br />
                <div className='ownerSupplier'>
                  <div>
                    <input type='radio' name='radio' value='restaurant owner' onChange={(e) => setChoose(e.target.value)} />
                    <label>Restaurant Owner</label>
                  </div>
                  <div>
                    <input type='radio' name='radio' value='supplier' onChange={(e) => setChoose(e.target.value)} />
                    <label>Supplier</label>
                  </div>
                </div>
              </div>

              <div className='quiz-btn-section'>
                {questions.length > 0 && currentQuestionIndex < questions.length ? (
                  <>
                    <h3>{questions[currentQuestionIndex].question}</h3>
                    {questions[currentQuestionIndex].options.map((option, index) => (
                      <div key={index}>
                        <input type='radio' name='option' value={option} />
                        <label>{option}</label>
                      </div>
                    ))}
                    <button type='button' className='btn btn-primary quiz-btn' onClick={handleSubmit}>
                      {currentQuestionIndex === questions.length - 1 ? 'Submit' : 'Next'}
                    </button>
                  </>
                ) : null}
              </div>
            </form>
          ) : ( // Render "Thank You" message if submitted
            <div className="text-center">
              <h2>Thank You!</h2>
              <p>Your survey has been submitted successfully.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Choose;
