import React, { useState } from 'react'
import './Choose.css'
import axios from 'axios';

function Choose() {
  const [choose, setChoose] = useState('')
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffec(() => {
    // Fetch questions based on user selection
    fetchQuestions();
  }, [choose]);

  const fetchQuestions = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/questions/${choose}`);
      setQuestions(response.data.questions);
    } catch (error) {
      console.error('Error fetching questions:', error);
    }
  };

  const handleNextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
  };

  return (
    <div>
      <div className="container">
        <div className="content-div">
          <form>
            <h2 className="text-center mb-5">Business Survey</h2>
            <div className="col-lg-8 col-md-12 col-sm-12 mx-auto mb-5">
              <label className="form-label chooseQuestion fw-bold mb-5">Are you a restaurant owner or a supplier ?</label> <br />
              <div className='ownerSupplier'>
                <div>
                  <input type='radio' name='radio' value='restaurant owner' onChange={(e) => setChoose(e.target.value)} />
                  <label>Restaurant Owner</label>
                </div>
                <div>
                  <input type='radio' name='radio' value='supplier' onChange={(e) => setChoose(e.target.value)} />
                  <label>Supplier</label>
                </div>
              </div>
            </div>

            {/* <div className="quiz-btn-section ">
              <button type='submit' className="btn btn-primary second-next-btn" >Next</button>
            </div> */}

<div className='quiz-btn-section'>
              {questions.length > 0 && currentQuestionIndex < questions.length ? (
                <>
                  <h3>{questions[currentQuestionIndex].question}</h3>
                  {questions[currentQuestionIndex].options.map((option, index) => (
                    <div key={index}>
                      <input type='radio' name={`option${currentQuestionIndex}`} value={option} />
                      <label>{option}</label>
                    </div>
                  ))}
                  <button type='button' className='btn btn-primary quiz-btn' onClick={handleNextQuestion}>
                    Next
                  </button>
                </>
              ) : (
                <button type='submit' className='btn btn-primary quiz-btn'>
                  Submit
                </button>
              )}
            </div>

          </form>
        </div>
      </div>
    </div>
  )
}

export default Choose
